import React, { useState } from 'react';
import fetch from 'isomorphic-unfetch';

import Layout from '../components/Layout';
import Post from '../components/Post';

const Search = props => {

  const [searchString, setSearchString] = useState('');

  const submitData = async e => {
    e.preventDefault();
  }

  return (
    <div>
    <Layout>
      <div className='page'>
        <form onSubmit={submitData}>
          <h1>Search</h1>
          <input
            autoFocus
            onChange={e => setSearchString(e.target.value)}
            placeholder='Enter text'
            type='text'
            value={searchString}
          />
          <input
            disabled={!searchString}
            type='submit'
            value='Search'
          />
          <a className='back' href='#' onClick={() => Router.push('/')}>
            or Cancel
          </a>
        </form>
      </div>
      <style jsx>{`
        .page {
          background: white;
          padding: 3rem;
          display: flex;
          justify-content: center;
        }

        input[type='text'] {
          width: 100%;
          padding: 0.5rem;
          margin: 0.5rem 0;
          border-radius: 0.25rem;
          border: 0.125rem solid rgba(0, 0, 0, 0.2);
        }

        input[type='submit'] {
          background: #ececec;
          border: 0;
          padding: 1rem 2rem;
        }

        .back {
          margin-left: 1rem;
        }
      `}</style>
    </Layout>
    <Layout>
      <div className='page'>
        <form onSubmit={submitData}>
          <h1>Search</h1>
          <input
            autoFocus
            onChange={e => setSearchString(e.target.value)}
            placeholder='Enter text'
            type='text'
            value={searchString}
          />
          <input
            disabled={!searchString}
            type='submit'
            value='Search'
          />
          <a className='back' href='#' onClick={() => Router.push('/')}>
            or Cancel
          </a>
        </form>
      </div>
      <style jsx>{`
        .page {
          background: white;
          padding: 3rem;
          display: flex;
          justify-content: center;
        }

        input[type='text'] {
          width: 100%;
          padding: 0.5rem;
          margin: 0.5rem 0;
          border-radius: 0.25rem;
          border: 0.125rem solid rgba(0, 0, 0, 0.2);
        }

        input[type='submit'] {
          background: #ececec;
          border: 0;
          padding: 1rem 2rem;
        }

        .back {
          margin-left: 1rem;
        }
      `}</style>
    </Layout>
    </div>
  )
}

export const getServerSideProps = async context => {
  let posts = [];
  if (context.params && context.params.searchString) {
    const res = await fetch(`http://localhost:3000/api/filterPosts?searchString=${context.params.searchString}`);
    posts = await res.json();    
  }  

  return { props: { posts} };
}

export default Search;
